CREATE TABLE "orders" (
  "id" int NOT NULL,
  "user_id" int DEFAULT NULL,
  "total_price" decimal(10,2) NOT NULL,
  "status" varchar(30) DEFAULT 'đang chờ' CHECK ("status" IN ('đang chờ','đã xác nhận','đang vận chuyển','đã nhận hàng','hủy')),
