  "fullname" varchar(255) NOT NULL,
  "email" varchar(255) NOT NULL,
  "phone" varchar(10) NOT NULL,
  "address" text NOT NULL,
  "method_payment" varchar(30) DEFAULT 'tiền mặt' CHECK ("method_payment" IN ('tiền mặt','chuyển khoản','vnpay'))
);



