CREATE TABLE "products" (
  "id" int NOT NULL,
  "name" varchar(255)  DEFAULT NULL,
  "description" text ,
  "price" decimal(10,2) NOT NULL,
  "image" varchar(255)  DEFAULT NULL,
  "category_id" int DEFAULT NULL,
  "is_featured" BOOLEAN DEFAULT FALSE,
  "created_at" timestamp NULL DEFAULT CURRENT_TIMESTAMP,
  "discount_price" decimal(10,2) DEFAULT NULL,
  "brand_id" int DEFAULT NULL
);





CREATE TABLE "product_images" (
  "id" int NOT NULL,
  "product_id" int DEFAULT NULL,
  "image_path" varchar(255)  DEFAULT NULL
);





CREATE TABLE "reviews" (
  "id" int NOT NULL,
  "product_id" int NOT NULL,
  "user_id" int NOT NULL,
  "rating" smallint NOT NULL,
  "comment" text ,
  "created_at" timestamp NULL DEFAULT CURRENT_TIMESTAMP
);





CREATE TABLE "users" (
  "id" int NOT NULL,
  "username" varchar(50)  NOT NULL,
  "password" varchar(255)  NOT NULL,
  "email" varchar(100)  NOT NULL,
  "phone" varchar(15)  DEFAULT NULL,
  "role" varchar(30) check ("role" in ('customer','admin'))  DEFAULT 'customer',
  "created_at" timestamp NULL DEFAULT CURRENT_TIMESTAMP
);





-- Add PRIMARY KEYS
ALTER TABLE "cart" ADD PRIMARY KEY ("id");
ALTER TABLE "categories" ADD PRIMARY KEY ("id");
ALTER TABLE "contacts" ADD PRIMARY KEY ("id");
ALTER TABLE "orders" ADD PRIMARY KEY ("id");
ALTER TABLE "order_details" ADD PRIMARY KEY ("id");
ALTER TABLE "products" ADD PRIMARY KEY ("id");
ALTER TABLE "product_images" ADD PRIMARY KEY ("id");
ALTER TABLE "reviews" ADD PRIMARY KEY ("id");
ALTER TABLE "users" ADD PRIMARY KEY ("id");
ALTER TABLE "users" ADD CONSTRAINT "users_email_key" UNIQUE ("email");

-- Add indexes instead of ADD KEY (MySQL syntax)
CREATE INDEX idx_cart_user_id ON "cart" ("user_id");
CREATE INDEX idx_cart_product_id ON "cart" ("product_id");
CREATE INDEX idx_categories_parent_id ON "categories" ("parent_id");
CREATE INDEX idx_orders_user_id ON "orders" ("user_id");
CREATE INDEX idx_order_details_order_id ON "order_details" ("order_id");
CREATE INDEX idx_order_details_product_id ON "order_details" ("product_id");
CREATE INDEX idx_products_category_id ON "products" ("category_id");
CREATE INDEX idx_products_brand_id ON "products" ("brand_id");
CREATE INDEX idx_product_images_product_id ON "product_images" ("product_id");
CREATE INDEX idx_reviews_product_id ON "reviews" ("product_id");
CREATE INDEX idx_reviews_user_id ON "reviews" ("user_id");

-- Add foreign keys
ALTER TABLE "cart"
  ADD CONSTRAINT "cart_ibfk_1" FOREIGN KEY ("user_id") REFERENCES "users" ("id"),
  ADD CONSTRAINT "cart_ibfk_2" FOREIGN KEY ("product_id") REFERENCES "products" ("id");

ALTER TABLE "categories"
  ADD CONSTRAINT "categories_ibfk_1" FOREIGN KEY ("parent_id") REFERENCES "categories" ("id") ON DELETE SET NULL;

ALTER TABLE "orders"
  ADD CONSTRAINT "orders_ibfk_1" FOREIGN KEY ("user_id") REFERENCES "users" ("id");

ALTER TABLE "order_details"
  ADD CONSTRAINT "order_details_ibfk_1" FOREIGN KEY ("order_id") REFERENCES "orders" ("id"),
  ADD CONSTRAINT "order_details_ibfk_2" FOREIGN KEY ("product_id") REFERENCES "products" ("id");

ALTER TABLE "products"
  ADD CONSTRAINT "products_ibfk_1" FOREIGN KEY ("category_id") REFERENCES "categories" ("id"),
  ADD CONSTRAINT "products_ibfk_2" FOREIGN KEY ("brand_id") REFERENCES "brands" ("id");

ALTER TABLE "product_images"
  ADD CONSTRAINT "product_images_ibfk_1" FOREIGN KEY ("product_id") REFERENCES "products" ("id");

ALTER TABLE "reviews"
  ADD CONSTRAINT "reviews_ibfk_1" FOREIGN KEY ("product_id") REFERENCES "products" ("id"),
  ADD CONSTRAINT "reviews_ibfk_2" FOREIGN KEY ("user_id") REFERENCES "users" ("id");

